class Multiplication:
    """
    Instantiate a multiplication operation.
    Numbers will be multiplied by the given multiplier.

    :param multiplier: The multiplier.
    :type multiplier: int
    """

    def __init__(self, num1):
        self.num = num1

    def multiply(self, num2):
        """
        Multiply one number by another.

        :param num1: The first number to multiply
        :type num1: int

        :param num2: The number you multiply num1 by
        :type num2: int
        """
        return self.num * num2





