class Calculator:
        """
        Calculator having basic methods like addition, subtraction, multiplication and division.
        """
        def add(self, a, b):
            """
            Adds two numbers
            """
            return a + b

        def sub(self, a, b):
            """
            Subtracts two numbers
            """
            return a - b

        def mul(self, a, b):
            """
            Multiplies two numbers
            """
            return a*b

        def div(self, a, b):
            """
            Divides two numbers
            """
            return a/b
